        <div>
          {posts.map(
            (
              {
                title,
                description,
                children,
                slug,
                editedAt,
                author,
                authorLink,
              },
              index
            ) => {
              return (
                <div key={index} className="mb-12">
                  <div
                    className={`flex flex-col-reverse items-center pb-12 ${
                      index % 2 ? "md:flex-row-reverse" : "md:flex-row"
                    }`}
                  >
                    <div
                      className={`flex-1 min-w-0 border-solid border-neutral-200 ${
                        index % 2
                          ? "md:pl-16 md:border-l-[1px]"
                          : "md:pr-16 md:border-r-[1px]"
                      }`}
                    >
                      <div className="mb-6 font-mono text-neutral-600">
                        {new Intl.DateTimeFormat("zh-CN", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }).format(new Date(editedAt))}
                        <a
                          className="ml-4 hover:text-blue-500 hover:underline hover:decoration-dotted hover:decoration-current hover:underline-offset-4 transition-all"
                          target="_blank"
                          href={authorLink}
                        >
                          @{author}
                        </a>
                      </div>
                      <NextLink
                        href={slug}
                        className="block mb-8 font-semibold text-2xl md:text-3xl hover:text-blue-500 active:text-blue-500 transition-all"
                      >
                        {title}
                      </NextLink>
                      <div className="text-sm text-neutral-600 whitespace-break-spaces">
                        {description}
                      </div>
                      <NextLink
                        href={slug}
                        className="inline-flex items-center mt-8 md:mt-16 gap-2 text-sm text-neutral-700 hover:text-blue-500 font-medium hover:underline hover:decoration-dotted hover:decoration-current hover:underline-offset-4 transition-all"
                      >
                        阅读文章
                        <svg
                          stroke="currentColor"
                          fill="currentColor"
                          strokeWidth="0"
                          viewBox="0 0 16 16"
                          width="1em"
                          height="1em"
                        >
                          <path
                            fillRule="evenodd"
                            d="M1 8a.5.5 0 0 1 .5-.5h11.793l-3.147-3.146a.5.5 0 0 1 .708-.708l4 4a.5.5 0 0 1 0 .708l-4 4a.5.5 0 0 1-.708-.708L13.293 8.5H1.5A.5.5 0 0 1 1 8z"
                          ></path>
                        </svg>
                      </NextLink>
                    </div>
                    <div
                      className={`flex-1 min-w-0 w-full pb-8 max-w-[300px] md:pb-0 md:max-w-[initial] md:w-auto ${
                        index % 2 ? "md:pr-16" : "md:pl-16"
                      }`}
                    >
                      {children}
                    </div>
                  </div>
                  <hr
                    className="flex-none h-[1px] bg-neutral-300"
                    style={{
                      maskImage:
                        "linear-gradient(90deg, transparent, rgb(255, 255, 255) 20%, rgb(255, 255, 255) calc(100% - 20%), transparent)",
                    }}
                  />
                </div>
              )
            }
          )}
        </div>
